using System;

using easygenerator.Infrastructure.ImageProcessors;
using FluentAssertions;
using ImageMagick;
using Microsoft.VisualStudio.TestTools.UnitTesting;
using NSubstitute;

namespace easygenerator.Infrastructure.Tests.ImageProcessors
{
    [TestClass]
    public class MagickImageResizerTests
    {
        private const string imageSquareBase64 = "";

        private const string imageWithBigWidthBase64 = "";

        private MagickImageResizer _magickImageResizer;

        [TestInitialize]
        public void InitializeContext()
        {
            _magickImageResizer = Substitute.For<MagickImageResizer>();
        }

        #region ResizeImage

        [TestMethod]
        public void ResizeImage_ShouldTrowArgumentException_WhenPathIsInvalid()
        {
            //Arrange
            var path = "path";

            //Act
            Action action = () => _magickImageResizer.ResizeImage(path, Arg.Any<int>(), Arg.Any<int>(), Arg.Any<bool>());

            //Assert
            action.ShouldThrow<ArgumentException>();
        }

        #endregion ResizeImage

        #region MagickImageExtensions.Resize

        [TestMethod]
        public void Resize_ShouldNotChangeImageSize_WhenImageIsSmallerThanRequested_WhenScaleBySmallerSide()
        {
            var image = new MagickImage(Convert.FromBase64String(imageWithBigWidthBase64));
            var width = image.Width;
            var height = image.Height;
            //Act
            image.Resize(width + 10, height + 10, true);

            //Assert
            image.Width.Should().Be(width);
            image.Height.Should().Be(height);
        }

        [TestMethod]
        public void Resize_ShouldNotChangeImageSize_WhenImageIsSmallerThanRequested_WhenScaleByBiggerSide()
        {
            var image = new MagickImage(Convert.FromBase64String(imageWithBigWidthBase64));
            var width = image.Width;
            var height = image.Height;
            //Act
            image.Resize(width + 10, height + 20, false);

            //Assert
            image.Width.Should().Be(width);
            image.Height.Should().Be(height);
        }

        [TestMethod]
        public void Resize_ShouldChangeImageSize_WhenImageIsBiggerThanRequested_WhenScaleBySmallerSide()
        {
            var image = new MagickImage(Convert.FromBase64String(imageWithBigWidthBase64));
            var width = image.Width - 10;
            var height = image.Height - 20;
            //Act
            image.Resize(width, height, true);

            //Assert
            image.Height.Should().Be(height);
        }

        [TestMethod]
        public void Resize_ShouldChangeImageSize_WhenImageIsBiggerThanRequested_WhenScaleByBiggerSide()
        {
            var image = new MagickImage(Convert.FromBase64String(imageWithBigWidthBase64));
            var width = image.Width - 10;
            var height = image.Height - 10;
            //Act
            image.Resize(width, height - 10, false);

            //Assert
            image.Width.Should().Be(width);
        }

        [TestMethod]
        public void Resize_ShouldKeepImagePropotrions_WhenImageIsBiggerThanRequested_WhenScaleByBiggerSide()
        {
            var image1 = new MagickImage(Convert.FromBase64String(imageSquareBase64));
            var image2 = new MagickImage(Convert.FromBase64String(imageSquareBase64));
            var imageSquareSideSize = image1.Width;
            
            //Act
            image1.Resize(imageSquareSideSize - 20, imageSquareSideSize - 10, false);
            image2.Resize(imageSquareSideSize - 10, imageSquareSideSize - 20, false);

            //Assert
            image1.Width.Should().Be(image1.Height);
            image2.Width.Should().Be(image2.Height);
        }

        [TestMethod]
        public void Resize_ShouldKeepImagePropotrions_WhenImageIsBiggerThanRequested_WhenScaleBySmallerSide()
        {
            var image1 = new MagickImage(Convert.FromBase64String(imageSquareBase64));
            var image2 = new MagickImage(Convert.FromBase64String(imageSquareBase64));
            var imageSquareSideSize = image1.Width;

            //Act
            image1.Resize(imageSquareSideSize - 20, imageSquareSideSize - 10, true);
            image2.Resize(imageSquareSideSize - 10, imageSquareSideSize - 20, true);

            //Assert
            image1.Width.Should().Be(image1.Height);
            image2.Width.Should().Be(image2.Height);
        }

        #endregion ResizeImage
    }
}
